import { Link } from "react-router-dom";
import { useState } from "react";
import "./css/Header.css";

const Header = () => {
  const [dropdown, setDropdown] = useState(null);

  const toggleDropdown = (menu) => {
    setDropdown((prev) => (prev === menu ? null : menu));
  };

  return (
    <header className="header">
      <div className="">
        {/* Logo */}
        <h1 className="text-xl font-bold">
          <Link to="/">Nexora Academy</Link>
        </h1>

        {/* Navigation */}
        <nav>
          <ul className="flex space-x-6 relative">
            {/* Home */}
            <li>
              <Link to="/" className="hover:underline">Home</Link>
            </li>

            {/* About */}
            <li>
              <Link to="/about" className="hover:underline">About</Link>
            </li>

            {/* Learning */}
            <li
              className="relative"
              onMouseEnter={() => toggleDropdown("learning")}
              onMouseLeave={() => setDropdown(null)}
            >
              <span className="cursor-pointer hover:underline">Learning</span>
              {dropdown === "learning" && (
                <ul className="absolute bg-white text-black mt-2 shadow-md p-2 rounded">
                  <li className="hover:bg-gray-200 p-2">
                    <Link to="/learning/curriculum">Curriculum</Link>
                  </li>
                  <li className="hover:bg-gray-200 p-2">
                    <Link to="/learning/extracurricular">Extracurricular</Link>
                  </li>
                  <li className="hover:bg-gray-200 p-2">
                    <Link to="/learning/assessment">Assessment</Link>
                  </li>
                </ul>
              )}
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
