const Home = () => {
    const carouselRef = useRef(null);

  // Add automatic carousel functionality for Latest News section
  useEffect(() => {
    const carousel = carouselRef.current;
    const items = carousel.querySelectorAll(".news-item");
    let currentIndex = 0;

    const interval = setInterval(() => {
      items.forEach((item, index) => {
        item.style.transform = `translateX(-${currentIndex * 100}%)`;
      });
      currentIndex = (currentIndex + 1) % items.length;
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home">
      {/* Header Section */}
      <header className="header">
        <h1>Your Bright Future is Our Success</h1>
        <p>Empowering future leaders at Nexora Academy</p>
      </header>

      {/* Mission Section */}
      <section className="mission-section">
        <h2>Our Mission</h2>
        <p>
          At Nexora Academy, we strive to nurture future leaders by instilling
          knowledge, values, and skills that drive success in academics and life...
        </p>
        <img src="./images/principal.jpg" alt="Principal's Picture" />
        <a href="/mission" className="discover-btn">Discover More</a>
      </section>

      {/* Latest News Section */}
      <section className="news-section">
        <h2>Latest News & Events</h2>
        <div className="news-carousel" ref={carouselRef}>
          <div className="news-item">Exciting Science Fair Happening This Week!</div>
          <div className="news-item">Admissions Open for the 2025 Academic Year</div>
          <div className="news-item">Parent-Teacher Conference Scheduled for Feb 5th</div>
        </div>
      </section>
    </div>
    );
}

export default Home;
